(window.webpackJsonp=window.webpackJsonp||[]).push([[193],{1620:function(_,v,p){"use strict";p.r(v);var t=p(1),n=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("h2",{attrs:{id:"_1977-总纲"}},[_._v("1977 总纲")]),_._v(" "),v("p",[_._v("​\t\t中国共产党是无产阶级的政党，是无产阶级的阶级组织的最高形式，是由无产阶级先进分子所组成的、领导无产阶级和革命群众对于阶级敌人进行战斗的朝气蓬勃的先锋队组织。")]),_._v(" "),v("p",[_._v("中国共产党在整个社会主义历史阶段的基本纲领，是坚持无产阶级专政下的继续革命，逐步消灭资产阶级和一切剥削阶级，用社会主义战胜资本主义。党的最终目的，是实现共产主义。")]),_._v(" "),v("p",[_._v("中国共产党的指导思想和理论基础是马克思主义、列宁主义、毛泽东思想。党坚持反对修正主义，反对教条主义和经验主义。党坚持辩证唯物主义和历史唯物主义的世界观，反对唯心主义和形而上学的世界观。")]),_._v(" "),v("p",[_._v("伟大的领袖和导师毛泽东主席，是中国共产党的缔造者，是当代最伟大的马克思列宁主义者。毛主席把马克思列宁主义的普遍真理和革命的具体实践相结合，在反对帝国主义和国内反动阶级的斗争中，在反对党内右的和“左”的机会主义路线的斗争中，在反对国际现代修正主义的斗争中，继承、捍卫和发展了马克思列宁主义。毛主席领导我党我军和我国人民，经过长期的革命斗争和革命战争，取得了新民主主义革命的彻底胜利，建立了无产阶级专政的中华人民共和国，接着又经过无产阶级和资产阶级激烈的复杂的斗争，经过史无前例的无产阶级文化大革命，取得了社会主义革命和社会主义建设的伟大胜利。毛主席的旗帜，是我们党团结战斗、争取胜利的伟大旗帜。")]),_._v(" "),v("p",[v("strong",[_._v("社会主义社会是一个相当长的历史阶段。在这个历史阶段中，始终存在着阶级、阶级矛盾和阶级斗争，存在着社会主义同资本主义两条道路的斗争，存在着资本主义复辟的危险性，还存在着帝国主义、社会帝国主义进行颠覆和侵略的威胁。这些矛盾，只能靠无产阶级专政下继续革命的理论和实践来解决。")])]),_._v(" "),v("p",[_._v("我国的无产阶级文化大革命，就是在社会主义条件下，无产阶级反对资产阶级和一切剥削阶级，巩固无产阶级专政，防止资本主义复辟的政治大革命。这种性质的政治大革命，今后还要进行多次。")]),_._v(" "),v("p",[_._v("中国共产党坚持党在整个社会主义历史阶段的基本路线。要正确区别和处理敌我矛盾和人民内部矛盾，巩固和加强无产阶级专政。要全心全意依靠工人阶级，依靠贫农下中农，团结广大知识分子和其他劳动群众，调动一切积极因素，发展工人阶级领导的以工农联盟为基础的革命统一战线。要坚持无产阶级民族政策，加强全国各族人民的大团结。要继续开展阶级斗争、生产斗争和科学实验三大革命运动，坚持独立自主、自力更生、勤俭建国的方针和备战、备荒、为人民的方针，鼓足干劲，力争上游，多快好省地建设社会主义。在本世纪内，党要领导全国各族人民把我国建设成为农业、工业、国防和科学技术现代化的社会主义强国。")]),_._v(" "),v("p",[_._v("中国共产党坚持无产阶级国际主义，反对大国沙文主义，坚决同全世界真正的马克思列宁主义政党和组织团结在一起，同全世界无产阶级、被压迫人民和被压迫民族团结在一起，为反对苏美两个超级大国的霸权主义，为打倒帝国主义、现代修正主义和各国反动派，为在地球上消灭人剥削人的制度，使整个人类都得到解放而共同奋斗。")]),_._v(" "),v("p",[_._v("思想上政治上的路线正确与否是决定一切的。全党同志必须全面地正确地贯彻执行毛主席的无产阶级革命路线，坚持要搞马克思主义不要搞修正主义，要团结不要分裂，要光明正大不要搞阴谋诡计的三项基本原则。对于违反三项基本原则的潮流，要有敢于反对的革命精神。")]),_._v(" "),v("p",[_._v("全党要坚持民主集中制的组织原则，实行在民主基础上的集中和在集中指导下的民主。要充分发扬党内民主，发挥全体党员和党的各级组织的积极性和创造性，反对官僚主义、命令主义和军阀主义。要严格遵守党的纪律，维护党的集中统一，加强党的团结，反对分裂党的行为和派别活动，反对向党闹独立性，反对无政府主义。在党内同志的关系上，要实行“知无不言，言无不尽”，“言者无罪，闻者足戒”的原则，要采取辩证的方法，从团结的愿望出发，经过批评或者斗争，分清是非，达到新的团结。要努力造成一个又有集中又有民主，又有纪律又有自由，又有统一意志、又有个人心情舒畅、生动活泼的政治局面。")]),_._v(" "),v("p",[_._v("党要认真执行无产阶级的“任人唯贤”的干部路线，反对资产阶级的“任人唯亲”的干部路线。要按照毛主席提出的五项条件，在群众斗争中培养和造就千百万无产阶级革命事业的接班人。要特别警惕个人野心家、阴谋家和两面派，防止这样的坏人篡夺党和国家的各级领导，保证党和国家各级领导的纯洁性。")]),_._v(" "),v("p",[_._v("全党必须保持和发扬群众路线、实事求是的优良传统，保持和发扬理论联系实际、密切联系群众、批评和自我批评的作风，保持和发扬谦虚谨慎、不骄不躁、艰苦奋斗的作风，防止党员、特别是党的领导干部利用职权谋取任何特权，同资产阶级思想和作风作坚决的斗争。")]),_._v(" "),v("p",[_._v("中国共产党党员，无论何时何地都要以个人利益服从党和人民的利益，不怕困难，不怕牺牲，为实现党的纲领积极工作，为共产主义事业奋斗终身。")]),_._v(" "),v("p",[_._v("中国共产党是伟大的、光荣的、正确的党，是全中国人民的领导核心。全党一定要永远高举和坚决捍卫马克思主义、列宁主义、毛泽东思想的伟大旗帜，保证我们党的事业继续沿着马克思主义路线胜利前进。")]),_._v(" "),v("h2",{attrs:{id:"_1982-总纲"}},[_._v("1982 总纲")]),_._v(" "),v("p",[_._v("中国共产党是中国工人阶级的先锋队，是中国各族人民利益的忠实代表，是中国社会主义事业的领导核心。党的最终目标，是实现共产主义的社会制度。")]),_._v(" "),v("p",[_._v("中国共产党以马克思列宁主义、毛泽东思想作为自己的行动指南。")]),_._v(" "),v("p",[_._v("马克思和恩格斯运用辩证唯物主义和历史唯物主义，分析资本主义社会的发展规律，创立了科学社会主义理论。按照这个理论，经过无产阶级革命斗争的胜利，资产阶级专政必然为无产阶级专政所代替，资本主义社会必然被改造为生产资料公有、消灭剥削、各尽所能、按劳分配的社会主义社会；社会主义社会经过生产力的巨大发展和思想、政治、文化的巨大进步，最后必然发展为各尽所能、按需分配的共产主义社会。进入二十世纪以后，列宁指出，资本主义已经发展到帝国主义阶段，无产阶级的解放斗争必然同世界被压迫民族的解放斗争联合起来，社会主义革命在帝国主义统治的薄弱环节有可能首先取得胜利。半个多世纪以来世界历史的进程，特别是社会主义制度在一些国家的建立和发展，证明了科学社会主义理论是正确的。")]),_._v(" "),v("p",[_._v("社会主义制度的发展和完善是一个长期的历史过程。从根本上说，社会主义制度消除了资本主义制度本身无法克服的固有矛盾，具有资本主义制度不可比拟的优越性。社会主义使人民真正成为国家的主人，日益摆脱剥削制度和生产资料私有制度形成的旧思想旧习惯，日益提高共产主义觉悟，日益形成共同的理想、共同的道德和共同的纪律。社会主义充分发挥人民的积极性、创造性，有计划、按比例、高速度地发展社会生产力，满足社会成员日益增长的物质文化生活的需要。社会主义事业正在向前发展，并且必将通过各国人民自愿选择的、适合本国特点的道路，逐步在全世界取得胜利。")]),_._v(" "),v("p",[_._v("以毛泽东同志为主要代表的中国共产党人，把马克思列宁主义的普遍原理同中国革命的具体实践结合起来，创立了毛泽东思想。毛泽东思想是马克思列宁主义在中国的运用和发展，是被实践证明了的关于中国革命和建设的正确的理论原则和经验总结，是中国共产党集体智慧的结晶。")]),_._v(" "),v("p",[_._v("中国共产党领导全国各族人民，经过长期的反对帝国主义、封建主义、官僚资本主义的革命斗争，取得了新民主主义革命的胜利，建立了人民民主专政的中华人民共和国；并且在建国以后，顺利地进行了社会主义改造，完成了从新民主主义到社会主义的过渡，确立了社会主义制度，发展了社会主义的经济、政治和文化。")]),_._v(" "),v("p",[v("strong",[_._v("在剥削阶级作为阶级消灭以后，我国社会存在的矛盾大多数不具有阶级斗争的性质，阶级斗争已经不是主要矛盾。由于国内的因素和国际的影响，阶级斗争还在一定范围内长期存在，在某种条件下还有可能激化。我国社会的主要矛盾是人民日益增长的物质文化需要同落后的社会生产之间的矛盾。其他矛盾应当在解决这个主要矛盾的同时加以解决。要严格区分和正确处理敌我矛盾和人民内部矛盾这两类不同性质的矛盾。")])]),_._v(" "),v("p",[_._v("中国共产党在现阶段的总任务是：团结全国各族人民，自力更生，艰苦奋斗，逐步实现工业、农业、国防和科学技术现代化，把我国建设成为高度文明、高度民主的社会主义国家。")]),_._v(" "),v("p",[_._v("中国共产党工作的重点，是领导全国各族人民进行社会主义现代化经济建设。应当大力发展社会生产力，并且按照生产力的实际水平和发展要求，逐步完善社会主义的生产关系。应当在生产发展和社会财富增长的基础上，逐步提高城乡人民的物质文化生活水平。")]),_._v(" "),v("p",[_._v("中国共产党领导人民在建设高度物质文明的同时，建设高度的社会主义精神文明。应当大力发展教育、科学、文化事业，用共产主义思想教育党员和人民群众，抵制和克服资本主义腐朽思想、封建主义残余思想和其他非无产阶级思想，努力使我国人民成为有理想、有道德、有文化、有纪律的人民。")]),_._v(" "),v("p",[_._v("中国共产党领导人民发展社会主义民主，健全社会主义法制，巩固人民民主专政。应当切实保障人民管理国家事务和社会事务、管理经济和文化事业的权利，坚决打击蓄意破坏我国社会主义制度的敌对分子和严重危害社会安全的分子。必须努力加强人民解放军的建设，巩固国防，随时准备抗击和歼灭入侵之敌。")]),_._v(" "),v("p",[_._v("中国共产党维护和发展国内各民族的平等、团结、互助关系，坚持实行民族区域自治政策，帮助各少数民族地区发展经济文化，积极培养、选拔少数民族干部。")]),_._v(" "),v("p",[_._v("中国共产党同全国各民族工人、农民、知识分子团结在一起，同各民主党派、无党派民主人士、各民族的爱国力量团结在一起，进一步发展和壮大由全体社会主义劳动者、拥护社会主义的爱国者、拥护祖国统一的爱国者组成的最广泛的爱国统一战线。要同全国人民包括台湾同胞、港澳同胞和国外侨胞一起，完成祖国统一的大业。")]),_._v(" "),v("p",[_._v("中国共产党对国际事务的基本立场是：坚持无产阶级国际主义，坚持同全世界无产阶级、被压迫民族、被压迫人民，以及一切爱好和平、主持正义的组织和人士的团结，共同反对帝国主义、霸权主义、殖民主义，维护世界和平，促进人类进步。在互相尊重主权和领土完整、互不侵犯、互不干涉内政、平等互利、和平共处五项原则的基础上，发展我国同世界各国的关系。在马克思主义基础上，按照独立自主、完全平等、互相尊重、互不干涉内部事务的原则，发展我党同各国共产党和其他工人阶级政党的关系。")]),_._v(" "),v("p",[_._v("中国共产党要领导全国各族人民实现社会主义现代化的宏伟目标，必须加强党的建设，发扬党的优良传统，提高党的战斗力，坚决实现以下三项基本要求：")]),_._v(" "),v("p",[_._v("第一，思想上政治上的高度一致。中国共产党以实现共产主义为最高纲领，所有共产党员都必须为此而奋斗终身。在现阶段，坚持社会主义道路，坚持人民民主专政，坚持党的领导，坚持马克思列宁主义、毛泽东思想，集中力量进行社会主义现代化建设，是全党团结统一的政治基础。党的思想路线是一切从实际出发，理论联系实际，实事求是，在实践中检验真理和发展真理。全党必须依据这条思想路线，科学地总结历史经验，调查研究现实情况，解决国内和国际事务中提出的新问题，反对一切“左”的和右的错误倾向。")]),_._v(" "),v("p",[_._v("第二，全心全意为人民服务。党除了工人阶级和最广大人民群众的利益，没有自己特殊的利益。党的纲领和政策，正是工人阶级和最广大人民群众的根本利益的科学表现。党在领导群众为实现共产主义理想而奋斗的全部过程中，始终同群众同甘共苦，保持最密切的联系，不允许任何党员脱离群众，凌驾于群众之上。党坚持用共产主义思想教育群众，并在自己的工作中实行群众路线，一切为了群众，一切依靠群众，把党的正确主张变为群众的自觉行动。")]),_._v(" "),v("p",[_._v("第三，坚持民主集中制。党内充分发扬民主，在民主的基础上实行高度的集中，加强组织性纪律性，保证全党行动的一致，保证党的决定得到迅速有效的贯彻执行。党在自己的政治生活中正确地开展批评和自我批评，在原则问题上进行思想斗争，坚持真理，修正错误。实行在党的纪律面前人人平等的原则，给违犯纪律的党员以应有的批评或处分，把坚持反对党、危害党的分子清除出党。")]),_._v(" "),v("p",[_._v("党的领导主要是政治、思想和组织的领导。党必须制定和执行正确的路线、方针和政策，做好党的组织工作和宣传教育工作，发挥全体党员在一切工作和社会生活中的先锋模范作用。党必须在宪法和法律的范围内活动。党必须保证国家的立法、司法、行政机关，经济、文化组织和人民团体积极主动地、独立负责地、协调一致地工作。党必须加强对工会、共产主义青年团、妇女联合会等群众组织的领导，充分发挥它们的作用。共产党员只占全国人口中的少数，必须同党外群众亲密合作，共同促进社会主义祖国日益繁荣富强，直至最后实现共产主义。")])])}),[],!1,null,null,null);v.default=n.exports}}]);